INSERT INTO "character"("id","name","description") VALUES
(1, 'Jack Skellington','Héros principal du film, Jack Skellington est le roi des citrouilles au pays d’Halloween. Il sème la terreur chaque année à Halloween!'),
(2, 'Oogie Boogie','C"est le grand méchant de L"Etrange Noël de Monsieur Jack.Il vit à l"écart de Halloween Town où il passe son temps dans son casino.'),
(3, 'Sally','C’est plus qu’une héroïne amoureuse de Jack. Elle est son ange gardien.C"est une poupée de chiffon, faite de multiples morceaux cousus ensemble.'),
(4, 'Mayor', 'C"est le maire de Halloween Town. Il deux visages. Ces deux facettes possèdent chacune des personnalités totalement différentes.'),
(5, 'Dr Funklestein', 'C"est un scientifique plutôt grincheux vivant avec Sally, sa création, qu"il considère comme son assistante.'),
(6, 'Zero', 'Zéro est le petit chien fantôme de Jack. Il passe la majorité de son temps à dormir, notamment dans sa niche en forme de caveau funéraire.'),
(7, 'Barrel', 'Barrel est le plus jeune de la bande d"acolytes d"Oogie Boogie.'),
(8, 'Lock','Lock est le leader de la bande d"Oogie Boogie, mais ne réfléchit pas toujours.'),
(9, 'Shock', 'Shock est la plus rusée, la plus intelligente et la plus sceptique des sbires d"Oogie. '),
(10, 'Clown', 'Le clown chevauche un monocycle et peut se déchirer le visage pour révéler l"obscurité complète et disparaître dans une bouffée de fumée.'),
(11, 'Corpse Kid', 'Avec sa pâleur grise mortelle et ses yeux fermés cousus, Corpse Kid n"a pas besoin d"un costume pour devenir effrayant.'),
(12, 'Harlequin Demon', 'Le Harlequin Demon, comme le reste des habitants de Halloween Town, aime effrayer les autres et participer à de grands événements.'),
(13, 'Undersea Gal', 'Undersea Gal est un personnage de Halloween Town qui se cache dans le bassin de la place du village.'),
(14, 'Behemoth', 'Béhémoth est un habitant de Halloween Town avec une hache plantée dans la tête.'),
(15, 'Mr Hyde', 'Mr. Hyde se compose de trois personnages de tailles différentes, dont le plus petit se cache dans le chapeau du plus grand.'),
(16, 'Sax Player', 'Le Sax Player et son groupe mettent une ambiance funeste dans halloween town.'),
(17, 'Vampire', 'Le vampire a remporté le prix pour le sang drainé en une seule soirée et traine avec sa bande vampires'),
(18, 'Wolfman', 'C"est le loup garou de halloween town.');
